* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --primary-bg: #0a192f;
    --secondary-bg: #112240;
    --accent-color: #00d9ff;
    --text-primary: #ffffff;
    --text-secondary: #8892b0;
    --card-bg: rgba(17, 34, 64, 0.8);
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Poppins', sans-serif;
    background-color: var(--primary-bg);
    color: var(--text-primary);
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: rgba(10, 25, 47, 0.95);
    backdrop-filter: blur(10px);
    padding: 20px 0;
    z-index: 1000;
    box-shadow: 0 2px 20px rgba(0, 217, 255, 0.1);
}

.nav-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 24px;
    font-weight: 700;
    color: var(--accent-color);
    letter-spacing: 2px;
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 40px;
}

.nav-link {
    color: var(--text-primary);
    text-decoration: none;
    font-size: 14px;
    font-weight: 500;
    letter-spacing: 1px;
    transition: color 0.3s ease;
}

.nav-link:hover {
    color: var(--accent-color);
}

.btn-primary {
    background: var(--accent-color);
    color: var(--primary-bg);
    border: none;
    padding: 12px 30px;
    border-radius: 25px;
    font-weight: 600;
    font-size: 14px;
    cursor: pointer;
    letter-spacing: 1px;
    transition: all 0.3s ease;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(0, 217, 255, 0.4);
}

.hamburger {
    display: none;
    flex-direction: column;
    gap: 5px;
    cursor: pointer;
}

.hamburger span {
    width: 25px;
    height: 3px;
    background: var(--text-primary);
    transition: all 0.3s ease;
}

.hero {
    min-height: 100vh;
    display: flex;
    align-items: center;
    padding-top: 100px;
    position: relative;
    overflow: hidden;
}

.hero-container {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
    position: relative;
    z-index: 2;
}

.hero-label {
    color: var(--accent-color);
    font-size: 14px;
    font-weight: 500;
    letter-spacing: 2px;
    margin-bottom: 20px;
}

.hero-title {
    font-size: 56px;
    font-weight: 700;
    margin-bottom: 20px;
    line-height: 1.2;
}

.hero-subtitle {
    font-size: 32px;
    font-weight: 600;
    margin-bottom: 30px;
    color: var(--text-secondary);
}

.typing-text {
    color: var(--accent-color);
}

.cursor {
    animation: blink 0.7s infinite;
}

@keyframes blink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
}

.hero-description {
    color: var(--text-secondary);
    line-height: 1.8;
    margin-bottom: 40px;
    max-width: 500px;
}

.btn-secondary {
    background: transparent;
    color: var(--accent-color);
    border: 2px solid var(--accent-color);
    padding: 12px 35px;
    border-radius: 25px;
    font-weight: 600;
    font-size: 14px;
    cursor: pointer;
    letter-spacing: 1px;
    transition: all 0.3s ease;
    margin-bottom: 30px;
}

.btn-secondary:hover {
    background: var(--accent-color);
    color: var(--primary-bg);
    transform: translateY(-2px);
}

.social-icons {
    display: flex;
    gap: 20px;
}

.social-icon {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    background: var(--secondary-bg);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--accent-color);
    transition: all 0.3s ease;
}

.social-icon:hover {
    background: var(--accent-color);
    color: var(--primary-bg);
    transform: translateY(-3px);
}

.hero-image {
    display: flex;
    justify-content: center;
    align-items: center;
}

.image-wrapper {
    position: relative;
    width: 400px;
    height: 400px;
}

.image-glow {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 420px;
    height: 420px;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(0, 217, 255, 0.3) 0%, transparent 70%);
    animation: pulse 3s infinite;
}

@keyframes pulse {
    0%, 100% { transform: translate(-50%, -50%) scale(1); opacity: 0.5; }
    50% { transform: translate(-50%, -50%) scale(1.1); opacity: 0.8; }
}

.profile-img {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    object-fit: cover;
    border: 5px solid var(--accent-color);
    box-shadow: 0 0 40px rgba(0, 217, 255, 0.5);
    position: relative;
    z-index: 2;
}

.decorative-circles {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    overflow: hidden;
    z-index: 1;
}

.circle {
    position: absolute;
    border: 2px solid rgba(0, 217, 255, 0.1);
    border-radius: 50%;
}

.circle-1 {
    width: 500px;
    height: 500px;
    top: 10%;
    left: -10%;
}

.circle-2 {
    width: 300px;
    height: 300px;
    bottom: 20%;
    right: 10%;
}

.circle-3 {
    width: 200px;
    height: 200px;
    top: 40%;
    right: 30%;
}

.about {
    padding: 100px 0;
}

.about-card {
    background: var(--card-bg);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    padding: 60px;
    display: grid;
    grid-template-columns: 300px 1fr;
    gap: 60px;
    align-items: center;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.3);
}

.about-img {
    width: 100%;
    border-radius: 20px;
    box-shadow: 0 10px 30px rgba(0, 217, 255, 0.2);
}

.about-subtitle {
    color: var(--accent-color);
    font-size: 16px;
    font-weight: 600;
    letter-spacing: 2px;
    margin-bottom: 10px;
}

.about-title {
    font-size: 48px;
    font-weight: 700;
    margin-bottom: 20px;
}

.about-text {
    color: var(--text-secondary);
    line-height: 1.8;
    margin-bottom: 40px;
}

.stats {
    display: flex;
    gap: 40px;
    margin-bottom: 40px;
}

.stat-item {
    text-align: center;
}

.stat-number {
    font-size: 36px;
    font-weight: 700;
    color: var(--accent-color);
    margin-bottom: 5px;
}

.stat-label {
    color: var(--text-secondary);
    font-size: 14px;
    line-height: 1.4;
}

.btn-outline {
    background: transparent;
    color: var(--accent-color);
    border: 2px solid var(--accent-color);
    padding: 12px 35px;
    border-radius: 25px;
    font-weight: 600;
    font-size: 14px;
    cursor: pointer;
    letter-spacing: 1px;
    transition: all 0.3s ease;
}

.btn-outline:hover {
    background: var(--accent-color);
    color: var(--primary-bg);
    transform: translateY(-2px);
}

.projects {
    padding: 100px 0;
    background: var(--secondary-bg);
}

.section-header {
    text-align: center;
    margin-bottom: 60px;
}

.section-label {
    color: var(--accent-color);
    font-size: 14px;
    font-weight: 500;
    letter-spacing: 2px;
    margin-bottom: 10px;
}

.section-title {
    font-size: 48px;
    font-weight: 700;
}

.highlight {
    color: var(--accent-color);
}

.projects-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
    margin-bottom: 40px;
}

.project-card {
    background: var(--card-bg);
    backdrop-filter: blur(10px);
    border-radius: 15px;
    overflow: hidden;
    transition: all 0.3s ease;
}

.project-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 217, 255, 0.2);
}

.project-image {
    position: relative;
    overflow: hidden;
}

.project-image img {
    width: 100%;
    height: 250px;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.project-card:hover .project-image img {
    transform: scale(1.1);
}

.project-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 217, 255, 0.8);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.project-card:hover .project-overlay {
    opacity: 1;
}

.project-btn {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    background: white;
    border: none;
    color: var(--accent-color);
    cursor: pointer;
    transition: transform 0.3s ease;
}

.project-btn:hover {
    transform: rotate(45deg);
}

.project-content {
    padding: 25px;
}

.project-title {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 5px;
}

.project-description {
    color: var(--text-secondary);
    font-size: 14px;
}

.pagination {
    display: flex;
    justify-content: center;
    gap: 10px;
}

.dot {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    cursor: pointer;
    transition: all 0.3s ease;
}

.dot.active {
    background: var(--accent-color);
    width: 30px;
    border-radius: 5px;
}

.skills {
    padding: 100px 0;
}

.skills-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 30px;
}

.skill-card {
    background: var(--card-bg);
    backdrop-filter: blur(10px);
    border-radius: 15px;
    padding: 30px;
    text-align: center;
    transition: all 0.3s ease;
}

.skill-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 217, 255, 0.2);
}

.skill-icon {
    margin-bottom: 20px;
    color: var(--accent-color);
    display: flex;
    justify-content: center;
}

.skill-name {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 15px;
}

.skill-bar {
    width: 100%;
    height: 8px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 10px;
    overflow: hidden;
    margin-bottom: 10px;
}

.skill-progress {
    height: 100%;
    background: linear-gradient(90deg, var(--accent-color), #00b8d4);
    border-radius: 10px;
    transition: width 1s ease;
}

.skill-level {
    color: var(--text-secondary);
    font-size: 14px;
    font-weight: 500;
}

.testimonials {
    padding: 100px 0;
    background: var(--secondary-bg);
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
}

.testimonial-card {
    background: var(--card-bg);
    backdrop-filter: blur(10px);
    border-radius: 15px;
    padding: 35px;
    transition: all 0.3s ease;
}

.testimonial-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 40px rgba(0, 217, 255, 0.2);
}

.testimonial-rating {
    color: #ffd700;
    margin-bottom: 20px;
    font-size: 18px;
}

.testimonial-text {
    color: var(--text-secondary);
    line-height: 1.8;
    margin-bottom: 25px;
    font-style: italic;
}

.testimonial-author {
    display: flex;
    align-items: center;
    gap: 15px;
}

.testimonial-img {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    object-fit: cover;
    border: 3px solid var(--accent-color);
}

.testimonial-name {
    font-size: 16px;
    font-weight: 600;
    margin-bottom: 3px;
}

.testimonial-position {
    color: var(--text-secondary);
    font-size: 13px;
}

.star {
    font-size: 16px;
}

.contact {
    padding: 100px 0;
    background: var(--secondary-bg);
}

.contact-wrapper {
    display: grid;
    grid-template-columns: 400px 1fr;
    gap: 60px;
    background: var(--card-bg);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    padding: 60px;
}

.contact-image {
    margin-bottom: 30px;
}

.contact-image img {
    width: 100%;
    border-radius: 15px;
    box-shadow: 0 10px 30px rgba(0, 217, 255, 0.2);
}

.contact-item {
    margin-bottom: 30px;
}

.contact-label {
    color: var(--text-secondary);
    font-size: 12px;
    letter-spacing: 1px;
    margin-bottom: 5px;
}

.contact-value {
    color: var(--text-primary);
    font-size: 16px;
    font-weight: 500;
}

.contact-socials {
    display: flex;
    gap: 15px;
}

.contact-social {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    background: var(--accent-color);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--primary-bg);
    transition: all 0.3s ease;
}

.contact-social:hover {
    transform: translateY(-3px);
    box-shadow: 0 5px 15px rgba(0, 217, 255, 0.4);
}

.contact-title {
    font-size: 32px;
    font-weight: 700;
    margin-bottom: 15px;
}

.contact-subtitle {
    color: var(--text-secondary);
    line-height: 1.6;
    margin-bottom: 40px;
}

.contact-form {
    display: flex;
    flex-direction: column;
    gap: 20px;
}

.form-group input,
.form-group textarea {
    width: 100%;
    background: rgba(0, 217, 255, 0.05);
    border: 1px solid rgba(0, 217, 255, 0.2);
    border-radius: 8px;
    padding: 15px 20px;
    color: var(--text-primary);
    font-family: 'Poppins', sans-serif;
    font-size: 14px;
    transition: all 0.3s ease;
}

.form-group input::placeholder,
.form-group textarea::placeholder {
    color: var(--text-secondary);
    letter-spacing: 1px;
}

.form-group input:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--accent-color);
    background: rgba(0, 217, 255, 0.1);
}

.btn-submit {
    background: var(--accent-color);
    color: var(--primary-bg);
    border: none;
    padding: 15px 40px;
    border-radius: 25px;
    font-weight: 600;
    font-size: 14px;
    letter-spacing: 1px;
    cursor: pointer;
    transition: all 0.3s ease;
}

.btn-submit:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(0, 217, 255, 0.4);
}

.footer {
    background: #000;
    padding: 60px 0 20px;
}

.footer-content {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr 1.5fr;
    gap: 50px;
    margin-bottom: 40px;
}

.footer-logo {
    font-size: 28px;
    font-weight: 700;
    color: var(--accent-color);
    letter-spacing: 2px;
    margin-bottom: 15px;
}

.footer-desc {
    color: var(--text-secondary);
    line-height: 1.6;
    margin-bottom: 25px;
    font-size: 14px;
}

.footer-socials {
    display: flex;
    gap: 15px;
}

.footer-social-icon {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: var(--secondary-bg);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--accent-color);
    transition: all 0.3s ease;
}

.footer-social-icon:hover {
    background: var(--accent-color);
    color: var(--primary-bg);
    transform: translateY(-3px);
}

.footer-title {
    font-size: 18px;
    font-weight: 600;
    margin-bottom: 20px;
    color: var(--text-primary);
}

.footer-links {
    list-style: none;
}

.footer-links li {
    margin-bottom: 12px;
}

.footer-links a {
    color: var(--text-secondary);
    text-decoration: none;
    font-size: 14px;
    transition: color 0.3s ease;
}

.footer-links a:hover {
    color: var(--accent-color);
}

.footer-contact {
    list-style: none;
}

.footer-contact li {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 15px;
    color: var(--text-secondary);
    font-size: 14px;
}

.footer-contact svg {
    color: var(--accent-color);
}

.footer-bottom {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding-top: 30px;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.footer-copyright,
.footer-credit {
    color: var(--text-secondary);
    font-size: 13px;
}

.heart {
    color: #ff0066;
    animation: heartbeat 1.5s infinite;
}

@keyframes heartbeat {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
}

.toast {
    position: fixed;
    bottom: 30px;
    right: 30px;
    background: var(--accent-color);
    color: var(--primary-bg);
    padding: 20px 30px;
    border-radius: 50px;
    display: flex;
    align-items: center;
    gap: 15px;
    font-weight: 600;
    box-shadow: 0 10px 30px rgba(0, 217, 255, 0.4);
    transform: translateX(400px);
    transition: transform 0.3s ease;
    z-index: 2000;
}

.toast.show {
    transform: translateX(0);
}

.fade-in {
    opacity: 0;
    transform: translateY(30px);
    animation: fadeInUp 0.8s ease forwards;
}

@keyframes fadeInUp {
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@media (max-width: 968px) {
    .nav-menu {
        position: fixed;
        left: -100%;
        top: 80px;
        flex-direction: column;
        background: var(--secondary-bg);
        width: 100%;
        text-align: center;
        transition: 0.3s;
        padding: 30px 0;
    }

    .nav-menu.active {
        left: 0;
    }

    .hamburger {
        display: flex;
    }

    .hero-container {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .hero-image {
        order: -1;
    }

    .image-wrapper {
        width: 300px;
        height: 300px;
    }

    .image-glow {
        width: 320px;
        height: 320px;
    }

    .hero-title {
        font-size: 40px;
    }

    .hero-subtitle {
        font-size: 24px;
    }

    .social-icons {
        justify-content: center;
    }

    .about-card {
        grid-template-columns: 1fr;
        padding: 40px;
        text-align: center;
    }

    .stats {
        justify-content: center;
    }

    .projects-grid {
        grid-template-columns: 1fr;
    }

    .skills-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }

    .contact-wrapper {
        grid-template-columns: 1fr;
        padding: 40px;
    }

    .footer-content {
        grid-template-columns: 1fr 1fr;
        gap: 30px;
    }

    .footer-bottom {
        flex-direction: column;
        gap: 15px;
        text-align: center;
    }
}

@media (max-width: 600px) {
    .hero-title {
        font-size: 32px;
    }

    .about-title,
    .section-title {
        font-size: 32px;
    }

    .stats {
        flex-direction: column;
        gap: 20px;
    }

    .skills-grid {
        grid-template-columns: 1fr;
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .footer-socials {
        justify-content: center;
    }

    .image-wrapper {
        width: 250px;
        height: 250px;
    }

    .image-glow {
        width: 270px;
        height: 270px;
    }
}
